I'm in the middle of writing a game, but once I run it and start playing, before not very long the Quest game screen goes into &quot;Not Responding&quot; mode. Can anyone help? The ASL code is below:



' &quot;Cast your vote!&quot;
' Created with QDK 4.1.4

!include &lt;stdverbs.lib&gt;

define game &lt;Cast your vote!&gt;
	asl-version &lt;410&gt;
	start &lt;Cast your vote!&gt;
	game info &lt;Created with QDK 4.1.4&gt;
end define

define options
	debug on
	panes on
	abbreviations on
end define

define room &lt;Cast your vote!&gt;
	script {
		set numeric &lt;comtot; 0&gt;
		set numeric &lt;socTOT; 0&gt;
		set numeric &lt;libTOT; 0&gt;
		set numeric &lt;refTOT; 0&gt;
		set numeric &lt;conTOT; 0&gt;
		set numeric &lt;natTOT; 0&gt;
		set numeric &lt;youTOT; 0&gt;
		set numeric &lt;comCAL; 14&gt;
		set numeric &lt;socCAL; 15&gt;
		set numeric &lt;libCAL; 14&gt;
		set numeric &lt;refCAL; 14&gt;
		set numeric &lt;conCAL; 15&gt;
		set numeric &lt;natCAL; 14&gt;
		set numeric &lt;youCAL; 14&gt;
		set numeric &lt;turnpoints; 0&gt;
		msg &lt;Welcome to |bCast your vote! |xb&gt;
		wait &lt;Press any key to continue.&gt;
		do &lt;Points Allocation Proposal&gt;
	}
end define

define procedure &lt;Points Allocation Proposal&gt;
	set numeric &lt;compap; 0&gt;
	set numeric &lt;socPAP; 0&gt;
	set numeric &lt;libPAP; 0&gt;
	set numeric &lt;refPAP; 0&gt;
	set numeric &lt;conPAP; 0&gt;
	set numeric &lt;natPAP; 0&gt;
	set numeric &lt;youPAP; 0&gt;
	inc &lt;comTOT; %comCAL%&gt;
	inc &lt;socTOT; %socCAL%&gt;
	inc &lt;libTOT; %libCAL%&gt;
	inc &lt;refTOT; %refCAL%&gt;
	inc &lt;conTOT; %conCAL%&gt;
	inc &lt;natTOT; %natCAL%&gt;
	inc &lt;youTOT; %youCAL%&gt;
	do &lt;Points Allocation Proposal 2&gt;
end define

define procedure &lt;Points Allocation Proposal 2&gt;
	if ( %turnpoints% &lt;= 99 ) then set numeric &lt;random number; $rand (1; 7)$&gt; else {
		set numeric &lt;turnpoints; 0&gt;
		set numeric &lt;random number; 0&gt;
		msg &lt;The following points allocation has been proposed. The current allocation and the running totals are listed in brackets.|n|nCommunist Party -    %comPAP% (%comCAL%) (%comTOT%)|nSocialist Party -          %socPAP% (%socCAL%) (%socTOT%)|nLiberal Party -             %libPAP% (%libCAL%) (%libTOT%)|nReform Party -            %refPAP% (%refCAL%) (%refTOT%)|nConservative Party -  %conPAP% (%conCAL%) (%conTOT%)|nNationalist Party -      %natPAP% (%natCAL%) (%natTOT%)|n|nYou -                             %youPAP% (%youCAL%) (%youTOT%)&gt;
		do &lt;Voting&gt; }
	if ( %random number% = 1 ) then {
		inc &lt;compap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
	if ( %random number% = 2 ) then {
		inc &lt;socpap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
	if ( %random number% = 3 ) then {
		inc &lt;libpap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
	if ( %random number% = 4 ) then {
		inc &lt;refpap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
	if ( %random number% = 5 ) then {
		inc &lt;conpap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
	if ( %random number% = 6 ) then {
		inc &lt;natpap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
	if ( %random number% = 7 ) then {
		inc &lt;youpap; 1&gt;
		inc &lt;turnpoints; 1&gt;
		do &lt;Points Allocation Proposal 2&gt; }
end define

define procedure &lt;Voting&gt;
	set numeric &lt;yesvote; 0&gt;
	set numeric &lt;novote; 0&gt;
	set string &lt;comvote; abstain&gt;
	set string &lt;socvote; abstain&gt;
	set string &lt;libvote; abstain&gt;
	set string &lt;refvote; abstain&gt;
	set string &lt;convote; abstain&gt;
	set string &lt;natvote; abstain&gt;
	set string &lt;youvote; abstain&gt;
	msg &lt;Press &quot;y&quot; in order to vote yes to the proposal, or press any other key in order to vote no to it.&gt;
	enter &lt;yourvotechoice&gt;
	if ( #yourvotechoice# = y ) then {
		set string &lt;youvote; yes&gt;
		inc &lt;yesvote; %youtot%&gt; } else {
		set string &lt;youvote; no&gt;
		inc &lt;novote; %youtot%&gt; }
	if ( (%compap% * 3) + %socpap% &gt; (%comcal% * 3) + %soccal% ) then {
		set string &lt;comvote; yes&gt;
		inc &lt;yesvote; %comtot%&gt; } else {
		set string &lt;comvote; no&gt;
		inc &lt;novote; %comtot%&gt; }
	if ( (%socpap% * 3) + %compap% + %libpap% &gt; (%soccal% * 3) + %comcal% + %libcal% ) then {
		set string &lt;socvote; yes&gt;
		inc &lt;yesvote; %soctot%&gt; } else {
		set string &lt;socvote; no&gt;
		inc &lt;novote; %soctot%&gt; }
	if ( (%libpap% * 3) + %socpap% + %refpap% &gt; (%libcal% * 3) + %soccal% + %refcal% ) then {
		set string &lt;libvote; yes&gt;
		inc &lt;yesvote; %libtot%&gt; } else {
		set string &lt;libvote; no&gt;
		inc &lt;novote; %libtot%&gt; }
	if ( (%refpap% * 3) + %libpap% + %conpap% &gt; (%refcal% * 3) + %libcal% + %concal% ) then {
		set string &lt;refvote; yes&gt;
		inc &lt;yesvote; %reftot%&gt; } else {
		set string &lt;refvote; no&gt;
		inc &lt;novote; %reftot%&gt; }
	if ( (%conpap% * 3) + %refpap% + %natpap% &gt; (%concal% * 3) + %refcal% + %natcal% ) then {
		set string &lt;convote; yes&gt;
		inc &lt;yesvote; %contot%&gt; } else {
		set string &lt;convote; no&gt;
		inc &lt;novote; %contot%&gt; }
	if ( (%natpap% * 3) + %conpap% &gt; (%natcal% * 3) + %concal% ) then {
		set string &lt;natvote; yes&gt;
		inc &lt;yesvote; %nattot%&gt; } else {
		set string &lt;natvote; no&gt;
		inc &lt;novote; %nattot%&gt; }
	if ( %yesvote% &gt; %novote% ) then {
		msg &lt;The parties have voted for the proposal by %yesvote% to %novote%. The parties voted as follows:|n|nCommunist Party -          #comvote#|nSocialist Party -                #socvote#|nLiberal Party -                   #libvote#|nReform Party -                  #refvote#|nConservative Party -        #convote#|nNationalist Party -            #natvote#|n |nYou -                                  #youvote#&gt;
		set numeric &lt;comcal; %compap%&gt;
		set numeric &lt;soccal; %socpap%&gt;
		set numeric &lt;libcal; %libpap%&gt;
		set numeric &lt;refcal; %refpap%&gt;
		set numeric &lt;concal; %conpap%&gt;
		set numeric &lt;natcal; %natpap%&gt;
		set numeric &lt;youcal; %youpap%&gt;
		wait &lt;Press any key to continue&gt;
		do &lt;Points Allocation Proposal&gt; } else {
		msg &lt;The parties have voted against the proposal by %novote% to %yesvote%. The parties voted as follows:|n|nCommunist Party -          #comvote#|nSocialist Party -                #socvote#|nLiberal Party -                   #libvote#|nReform Party -                  #refvote#|nConservative Party -        #convote#|nNationalist Party -            #natvote#|n |nYou -                                  #youvote#&gt;
		wait &lt;Press any key to continue&gt;
		do &lt;Points Allocation Proposal&gt; }
end define
