	command &lt;leave&gt; if ask &lt;Are you sure you want to leave the admin room?&gt; then goto &lt;$objectproperty&#40;#name&#91;userid&#93;#; l_room&#41;$&gt;

	command &lt;/teleport #place#&gt; &#123;
