&lt;exit alias=&quot;Castle Street&quot; to=&quot;A0_Castle_Street&quot;&gt;
&lt;inherit name=&quot;eastdirection&quot; /&gt;
&lt;/exit&gt;

&lt;exit alias=&quot;Main square&quot; to=&quot;A0_Main_Square&quot;&gt;
