&lt;function name=&quot;cmb_setAttrib&quot; parameters=&quot;object,attrib,value&quot;&gt;
	if (cmb_chkObject(object)){
		attrib=cmb_chkAttrib(attrib)
		if (LengthOf(attrib)&gt;0) {
			set(object,attrib,value)
		}	
	}	
