      menulist = NewStringList()
      list add (menulist, &quot;Waar moet ik de delen van de code vinden?&quot;)
      list add (menulist, &quot;Jongetje, het toverstokje en de toverhoed&quot;)
      list add (menulist, &quot;Piraat &amp; het drinken&quot;)
