show menu (&quot;What order of ingrediants?&quot;,split(&quot;ABC;ACB;BAC;BCA;CAB;CBA&quot;,&quot;;&quot;),false) {
  if (result=ABC) {
    msg (you don't die; you chose the correct order of ingrediants!)
  } else {
    msg (you die; you didn't choose the correct order of ingrediants&#46;)
    finish
  }
