            battle_sequence (self,enemy)
          }
        }
        on ready {
          if (enemy&#46;dead = false) {
            if (enemy_battle_turn (self,enemy) = true) {
              msg (&quot;The round has ended&#46;&quot;)
            }
          }
        }
        on ready {
          battle_sequence (self,enemy)
        }
      }
      else if (GetInt (self,&quot;spd&quot;) &lt; GetInt (enemy,&quot;spd&quot;)) {
        on ready {
          msg (enemy&#46;alias + &quot; gets to go first for this round&#46;&quot;)
