              self&#46;defending = true
            }
          }
          case (&quot;Cast&quot;) {
            self&#46;defending = false
          }
          case (&quot;Item&quot;) {
            self&#46;defending = false
          }
          case (&quot;Run&quot;) {
            self&#46;defending = false
          }
