  &lt;parent type=&quot;object&quot;&gt;player&lt;/parent&gt;
  &lt;attr name=&quot;damage&quot; type=&quot;int&quot;&gt;1&lt;/attr&gt;
  &lt;equip type=&quot;script&quot;&gt;
