    if (this&#46;parent = player) {
      if (not player&#46;equipment_right_hand=axe) {
        player&#46;equipment_right_hand=axe
      } else {
        msg (&quot;You're already equipped with it&#46;&quot;)
      }
    }
  &lt;/equip&gt;
&lt;/object&gt;

