			}
		&lt;/script&gt;
	&lt;/command&gt;

	&lt;command name=&quot;remove&quot; template=&quot;remove&quot;&gt;
		&lt;multiple&gt;
			return (ScopeInventory())
		&lt;/multiple&gt;
		&lt;script&gt;
