define function &lt;desertroom&gt;
   set string &lt;returning; 1&gt;
   set numeric &lt;upper; $ubound&#40;oasis&#41;$&gt;
   for &lt;i; 1; %upper%&gt; &#123;
      set string &lt;dest; #game&#58;desert_x#,#game&#58;desert_y#&gt;
