        do (this, &quot;hug; squeeze&quot;)
      &lt;/pet&gt;
      &lt;hug type=&quot;script&quot;&gt;
        msg (&quot;Yuck&#46;  Cat hugged&#46;  Bleck&#46;&quot;)
