          inc &lt;time&#91;1&#93;&gt;
          if &#40;%time&#91;1&#93;% = 60&#41; then &#123;
               set &lt;%time&#91;1&#93;%; 0&gt;
               inc &lt;%time&#91;2&#93;%&gt;
               if &#40;%time&#91;2&#93;% = 60&#41; then &#123;
                    set &lt;%time&#91;2&#93;%; 0&gt;
                    inc &lt;%time&#91;3&#93;%&gt;
