define timer &lt;Time1&gt;
	interval &lt;0&gt;
	action {
		if ( %Time% = %Time2% ) then {
			set &lt;Time; 1&gt;
			timeron &lt;Time2&gt;
			timeroff &lt;Time1&gt;
		}
		else {
			inc &lt;Time&gt;
			timeron &lt;Time2&gt;
			timeroff &lt;Time1&gt;
		}
	}
	disabled
end define

define timer &lt;Time2&gt;
	interval &lt;0&gt;
	action {
		if ( %Time% = %Time2% ) then {
			set &lt;Time; 1&gt;
			timeron &lt;Time1&gt;
			timeroff &lt;Time2&gt;
		}
		else {
			inc &lt;Time&gt;
			if ( %Time% = %Timealso% ) then {
				if ( #Dayofweek# = #Dayofweeksun# ) then set &lt;Dayofweek; Monday&gt; else {
					if ( #Dayofweek# = #Dayofweekm# ) then set &lt;Dayofweek; Tuesday&gt; else {
