	set string &lt;Computer_Text; i will send you into orbit and await your commands there_&gt;
	do &lt;Computer_Text_Proc&gt;
	&#125;
	set string &lt;Computer_Text; prepare for lift off_&gt;
	do &lt;Computer_Text_Proc&gt;
	pause &lt;200&gt;
end define


