[code:140uor3f]if (global_data_object&#46;month_string = january or global_data_object&#46;month_string = march or global_data_object&#46;month_string = may or global_data_object&#46;month_string = july or global_data_object&#46;month_string = august or global_data_object&#46;month_string = october or global_data_object&#46;month_string = december) {
