		/* CSS handles the transition and timing&#46; */
		var bg = getComputedStyle(document&#46;body)&#46;getPropertyValue(&quot;background-color&quot;);
		document&#46;getElementById(&quot;theTitle&quot;)&#46;style&#46;color = bg;
