      msg (count + &quot;&#58; &lt;a class=\&quot;cmdlink\&quot; style=\&quot;&quot; + GetCurrentLinkTextFormat() + &quot;\&quot; onclick=\&quot;ASLEvent('ShowMenuResponse','&quot; + option + &quot;')\&quot;&gt;&quot; + optionText + &quot;&lt;/a&gt;&quot;)
