          player&#46;gender = result
          ShowMenu (&quot;Now, describe your age&#46;&quot;, Split (&quot;Teenanger;Young adult;Adult;Elder&quot;, &quot;;&quot;), false) {
            player&#46;age = result
            ClearScreen
            msg (&quot;&quot;)
