            msg (&quot;&quot;)
            msg (&quot;You are finished&#46; Press a key to begin&#46;&#46;&#46;&quot;)
            wait {
              ClearScreen
              DecreaseHealth (50)
              MoveObject (player, hallway)
            }
          }
        }
      }
    &lt;/enter&gt;
    &lt;beforeenter type=&quot;script&quot;&gt;
