Here is my current game code (all of it)  so you can see for yourself what I mean:
[code:3938i0fv]&lt;!--Saved by Quest 5&#46;6&#46;5621&#46;18142--&gt;
&lt;asl version=&quot;550&quot;&gt;
  &lt;include ref=&quot;English&#46;aslx&quot; /&gt;
  &lt;include ref=&quot;Core&#46;aslx&quot; /&gt;
  &lt;game name=&quot;Mazecraft&quot;&gt;
    &lt;gameid&gt;0b09d9d0-9005-467a-b255-0ebb4cf2929a&lt;/gameid&gt;
    &lt;version&gt;1&#46;0&lt;/version&gt;
    &lt;firstpublished&gt;2015&lt;/firstpublished&gt;
  &lt;/game&gt;
  &lt;object name=&quot;a0_0_0&quot;&gt;
    &lt;inherit name=&quot;editor_room&quot; /&gt;
    &lt;beforefirstenter type=&quot;script&quot;&gt;
      msg (&quot;4&quot;)
    &lt;/beforefirstenter&gt;
    &lt;beforeenter type=&quot;script&quot;&gt;
      msg (&quot;1&quot;)
    &lt;/beforeenter&gt;
    &lt;enter type=&quot;script&quot;&gt;
      msg (&quot;2&quot;)
    &lt;/enter&gt;
    &lt;onexit type=&quot;script&quot;&gt;
      msg (&quot;3&quot;)
    &lt;/onexit&gt;
    &lt;firstenter type=&quot;script&quot;&gt;
      msg (&quot;5&quot;)
    &lt;/firstenter&gt;
    &lt;object name=&quot;player&quot;&gt;
      &lt;inherit name=&quot;editor_object&quot; /&gt;
      &lt;inherit name=&quot;editor_player&quot; /&gt;
    &lt;/object&gt;
    &lt;exit alias=&quot;north&quot; to=&quot;player&quot;&gt;
      &lt;inherit name=&quot;northdirection&quot; /&gt;
      &lt;runscript /&gt;
      &lt;script type=&quot;script&quot;&gt;
        going (this&#46;alias, game&#46;pov&#46;parent)
      &lt;/script&gt;
    &lt;/exit&gt;
  &lt;/object&gt;
  &lt;type name=&quot;editor_room&quot;&gt;
    &lt;x type=&quot;int&quot;&gt;0&lt;/x&gt;
    &lt;y type=&quot;int&quot;&gt;0&lt;/y&gt;
    &lt;z type=&quot;int&quot;&gt;0&lt;/z&gt;
    &lt;beforefirstenter type=&quot;script&quot;&gt;
    &lt;/beforefirstenter&gt;
    &lt;firstenter type=&quot;script&quot;&gt;
    &lt;/firstenter&gt;
    &lt;enter type=&quot;script&quot;&gt;
    &lt;/enter&gt;
    &lt;onexit type=&quot;script&quot;&gt;
    &lt;/onexit&gt;
    &lt;beforeenter type=&quot;script&quot;&gt;
    &lt;/beforeenter&gt;
    &lt;changedparent type=&quot;script&quot;&gt;
      if (game&#46;pov = this) {
        if (IsDefined(&quot;oldvalue&quot;)) {
          OnEnterRoom (&quot;oldvalue&quot;)
        }
        else {
          OnEnterRoom (null)
        }
        if (game&#46;gridmap) {
          MergePOVCoordinates
        }
      }
    &lt;/changedparent&gt;
    &lt;visited type=&quot;boolean&quot;&gt;false&lt;/visited&gt;
  &lt;/type&gt;
  &lt;type name=&quot;gen_north&quot;&gt;
    &lt;inherit name=&quot;editor_room&quot; /&gt;
  &lt;/type&gt;
  &lt;type name=&quot;gen_east&quot;&gt;
    &lt;inherit name=&quot;editor_room&quot; /&gt;
  &lt;/type&gt;
  &lt;type name=&quot;gen_south&quot;&gt;
    &lt;inherit name=&quot;editor_room&quot; /&gt;
  &lt;/type&gt;
  &lt;type name=&quot;gen_west&quot;&gt;
    &lt;inherit name=&quot;editor_room&quot; /&gt;
  &lt;/type&gt;
  &lt;function name=&quot;going&quot; parameters=&quot;direction, currentroom&quot;&gt;
    if (direction = &quot;north&quot;) {
      x = currentroom&#46;x
      y = currentroom&#46;y + 1
      z = currentroom&#46;z
      direction2 = &quot;south&quot;
    }
    else if (direction = &quot;east&quot;) {
      x = currentroom&#46;x + 1
      y = currentroom&#46;y
      z = currentroom&#46;z
      direction2 = &quot;west&quot;
    }
    else if (direction = &quot;south&quot;) {
      x = currentroom&#46;x
      y = currentroom&#46;y - 1
      z = currentroom&#46;z
      direction2 = &quot;north&quot;
    }
    else if (direction = &quot;west&quot;) {
      x = currentroom&#46;x - 1
      y = currentroom&#46;y
      z = currentroom&#46;z
      direction2 = &quot;east&quot;
    }
    nextroom = (&quot;a&quot; + x + &quot;_&quot; + y + &quot;_&quot; + z)
    already_exist = GetObject(nextroom)
    if (already_exist = null) {
      create (nextroom, direction + &quot;direction&quot;)
    }
    else {
      MoveObject (player, nextroom)
    }
  &lt;/function&gt;
&lt;/asl&gt;[/code:3938i0fv]
