&lt;command name=&quot;character_information_command&quot;&gt;
  &lt;pattern&gt;info #object_parameter#&lt;/pattern&gt;
  &lt;script&gt;
    if (GetString (object_parameter, &quot;type_of_object&quot;) = &quot;party_member&quot;) {
