      msg (&quot;Race&#58; &quot; + object_parameter&#46;race)
      msg (&quot;Class&#58; &quot; + object_parameter&#46;class)
      // etc etc etc character stats
      wait {
        ClearScreen
      }
    } else {
      msg (&quot;You can only view your party members stat information&#46; Wrong input, try again&quot;)
    }
  &lt;/script&gt;
&lt;/command&gt;[/code:2puqa96a]

